import json
import re

class Task04:

    def __init__(self, path_for_input_file):
        self.path_for_input_file = path_for_input_file

    def read_input_file_to_elements_list(self):
        elements = str()
        with open(self.path_for_input_file, "r") as input_file:
            for line in input_file:
                elements = elements + line
        return re.findall('[A-Z][^A-Z]*', elements)


    def find_elements_full_names(self, elements_list):
        periodic_table = json.load(open('periodic_table.json'))

        answer = str()
        for item in elements_list:
            answer = answer + periodic_table[item]
        print(answer)

PATH_INP_1 = "./import_file_3.txt"
task_04 = Task04(PATH_INP_1)
task_04.find_elements_full_names(task_04.read_input_file_to_elements_list())

